<?php/** * Module DateTime; * Модуль для преобразования строк с датой в строку другого вида * @author Alexey iP Subbota * @version 0.1 * format: *   */class module_datetime{  private $month_short = array('Янв', 'Фев', 'Март', 'Апр', 'Май', 'Июнь', 'Июль' , 'Авг', 'Сен', 'Окт', 'Ноя', 'Дек');  private $month_short_rp = array('Янв', 'Фев', 'Марта', 'Апр', 'Мая', 'Июня', 'Июля' , 'Авг', 'Сен', 'Окт', 'Ноя', 'Дек');  private $month_full = array('Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь', 'Июль' , 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь');  private $month_full_rp = array('Января', 'Февраля', 'Марта', 'Апреля', 'Мая', 'Июня', 'Июля' , 'Августа', 'Сентября', 'Октября', 'Ноября', 'Декабря');  /**   * Конструктор   */  public function __construct()  {  }  /**   * Преобразование строки с датой и временем в удобный нам вид   * @param string datetime - строка с датой и временем (YYYY-mm-dd HH:ii:ss)   * @param string format - формат вывода   * @result string   */  public function convert($datetime = false, $format = false)  {    if ($datetime===false || $format===false) {      return false;    }    $result = false;    $parse = explode(' ', $datetime);    $date = explode('-', $parse[0]);    $time = explode(':', $parse[1]);    $date[1]--;    $result = str_replace('d', intval($date[2]), $format);    $result = str_replace('D', $date[2], $result);    $result = str_replace('mi', $date[1], $result);    $result = str_replace('mrl', mb_strtolower($this->month_short_rp[$date[1]]), $result);    $result = str_replace('mru', mb_strtoupper($this->month_short_rp[$date[1]]), $result);    $result = str_replace('mr', $this->month_short_rp[$date[1]], $result);    $result = str_replace('ml', mb_strtolower($this->month_short[$date[1]]), $result);    $result = str_replace('mu', mb_strtoupper($this->month_short[$date[1]]), $result);    $result = str_replace('m', $this->month_short[$date[1]], $result);    $result = str_replace('Mrl', mb_strtolower($this->month_full_rp[$date[1]]), $result);    $result = str_replace('Mru', mb_strtoupper($this->month_full_rp[$date[1]]), $result);    $result = str_replace('Mr', $this->month_full_rp[$date[1]], $result);    $result = str_replace('Ml', mb_strtolower($this->month_full[$date[1]]), $result);    $result = str_replace('Mu', mb_strtoupper($this->month_full[$date[1]]), $result);    $result = str_replace('M', $this->month_full[$date[1]], $result);    $result = str_replace('y', ($date[0][2] . $date[0][3]), $result);    $result = str_replace('Y', $date[0], $result);    $result = str_replace('h', intval($time[0]), $result);    $result = str_replace('H', $time[0], $result);    $result = str_replace('i', $time[1], $result);    $result = str_replace('s', $time[2], $result);    return $result;  }}